import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Card, CardContent } from '@/components/ui/card';
import { 
  ZapIcon, 
  Scale, 
  CalendarIcon, 
  UsersIcon, 
  CheckCircleIcon, 
  FileTextIcon, 
  FolderIcon, 
  DownloadIcon, 
  BookOpenIcon,
  InfoIcon 
} from 'lucide-react';

interface ResponseCardProps {
  content: string;
  sources?: any[];
  metadata?: any;
  verificationStatus?: string;
  isStreaming?: boolean;
}

export default function ResponseCard({ 
  content, 
  sources = [], 
  metadata, 
  verificationStatus, 
  isStreaming = false 
}: ResponseCardProps) {
  // Parse structured content for legal responses
  const parseContent = (text: string) => {
    const sections = {
      quickAnswer: '',
      facts: '',
      issues: '',
      decision: '',
      reasoning: '',
      statutes: '',
      precedents: '',
      application: '',
    };

    // Simple parsing - in production, this would be more sophisticated
    if (text.includes('Quick Answer') || text.includes('QUICK ANSWER')) {
      const match = text.match(/Quick Answer:?\s*(.+?)(?=\n\n|\n[A-Z]|$)/s);
      if (match) sections.quickAnswer = match[1].trim();
    }

    if (text.includes('Facts') || text.includes('FACTS')) {
      const match = text.match(/Facts:?\s*(.+?)(?=\n\n[A-Z]|$)/s);
      if (match) sections.facts = match[1].trim();
    }

    if (text.includes('Issues') || text.includes('ISSUES')) {
      const match = text.match(/Issues?:?\s*(.+?)(?=\n\n[A-Z]|$)/s);
      if (match) sections.issues = match[1].trim();
    }

    if (text.includes('Decision') || text.includes('DECISION')) {
      const match = text.match(/Decision:?\s*(.+?)(?=\n\n[A-Z]|$)/s);
      if (match) sections.decision = match[1].trim();
    }

    return { sections, fullText: text };
  };

  const { sections, fullText } = parseContent(content);

  // Extract citations from content
  const extractCitations = (text: string) => {
    const citations = [];
    const patterns = [
      /\b\d{4}\s*\)\s*\d+\s+SCC\s+\d+/g,
      /AIR\s+\d{4}\s+SC\s+\d+/g,
      /Section\s+\d+\w*\s+\w+/g,
    ];

    patterns.forEach(pattern => {
      const matches = text.match(pattern);
      if (matches) {
        citations.push(...matches);
      }
    });

    return [...new Set(citations)]; // Remove duplicates
  };

  const citations = extractCitations(content);

  return (
    <Card className="bg-card border border-border rounded-xl overflow-hidden" data-testid="response-card">
      {/* Quick Answer Section */}
      {sections.quickAnswer && (
        <div className="p-4 bg-muted/30">
          <div className="flex items-start gap-2 mb-2">
            <ZapIcon className="w-5 h-5 text-accent mt-0.5" />
            <h3 className="font-serif font-semibold text-base text-foreground">Quick Answer</h3>
          </div>
          <p className="text-sm text-foreground leading-relaxed">
            {sections.quickAnswer}
            {isStreaming && <span className="streaming-cursor"></span>}
          </p>
        </div>
      )}

      {/* Metadata Section */}
      {metadata && (
        <div className="px-4 py-3 border-t border-border bg-muted/10">
          <div className="flex flex-wrap gap-2">
            {metadata.court && (
              <Badge variant="outline" className="text-xs">
                <Scale className="w-3 h-3 mr-1" />
                {metadata.court}
              </Badge>
            )}
            {metadata.year && (
              <Badge variant="outline" className="text-xs">
                <CalendarIcon className="w-3 h-3 mr-1" />
                {metadata.year}
              </Badge>
            )}
            {metadata.bench && (
              <Badge variant="outline" className="text-xs">
                <UsersIcon className="w-3 h-3 mr-1" />
                {metadata.bench}
              </Badge>
            )}
            <div className="ml-auto">
              {verificationStatus === 'verified' && (
                <div className="inline-flex items-center gap-1 px-2 py-1 bg-green-50 text-green-700 border border-green-200 rounded text-xs">
                  <CheckCircleIcon className="w-3 h-3" />
                  <span>Verified</span>
                </div>
              )}
            </div>
          </div>
        </div>
      )}

      {/* Main Content */}
      <CardContent className="p-4 space-y-4">
        {sections.facts && (
          <div>
            <h4 className="font-serif font-semibold text-sm text-foreground mb-2 flex items-center gap-2">
              <span className="w-1 h-4 bg-secondary rounded-full"></span>
              Facts of the Case
            </h4>
            <div className="ml-3 text-sm text-foreground leading-relaxed whitespace-pre-line">
              {sections.facts}
            </div>
          </div>
        )}

        {sections.issues && (
          <div>
            <h4 className="font-serif font-semibold text-sm text-foreground mb-2 flex items-center gap-2">
              <span className="w-1 h-4 bg-accent rounded-full"></span>
              Legal Issues Framed
            </h4>
            <div className="ml-3 text-sm text-foreground leading-relaxed whitespace-pre-line">
              {sections.issues}
            </div>
          </div>
        )}

        {sections.decision && (
          <div>
            <h4 className="font-serif font-semibold text-sm text-foreground mb-2 flex items-center gap-2">
              <span className="w-1 h-4 bg-green-600 rounded-full"></span>
              Decision & Key Directions
            </h4>
            <div className="bg-green-50 border border-green-200 rounded-lg p-3">
              <div className="text-sm text-green-900 leading-relaxed whitespace-pre-line">
                {sections.decision}
              </div>
            </div>
          </div>
        )}

        {/* Default content if no structured sections */}
        {!sections.quickAnswer && !sections.facts && !sections.issues && !sections.decision && (
          <div className="text-sm text-foreground leading-relaxed whitespace-pre-line">
            {fullText}
            {isStreaming && <span className="streaming-cursor"></span>}
          </div>
        )}

        {/* Citations Section */}
        {citations.length > 0 && (
          <div>
            <h4 className="font-serif font-semibold text-sm text-foreground mb-2 flex items-center gap-2">
              <span className="w-1 h-4 bg-secondary rounded-full"></span>
              Citations Referenced
            </h4>
            <div className="flex flex-wrap gap-2">
              {citations.map((citation, index) => (
                <button
                  key={index}
                  className="citation-chip bg-secondary/10 text-secondary border border-secondary/20 hover:bg-secondary/20 transition-all duration-200"
                  data-testid={`citation-${index}`}
                >
                  {citation}
                </button>
              ))}
            </div>
          </div>
        )}
      </CardContent>

      {/* Sources Section */}
      {sources.length > 0 && (
        <div className="px-4 py-3 border-t border-border bg-muted/10">
          <div className="flex items-center gap-2 mb-2">
            <BookOpenIcon className="w-4 h-4 text-muted-foreground" />
            <h5 className="text-xs font-semibold text-muted-foreground uppercase tracking-wider">
              Sources
            </h5>
          </div>
          <div className="flex flex-wrap gap-2">
            {sources.map((source, index) => (
              <Badge
                key={index}
                variant="outline"
                className="text-xs font-mono"
                data-testid={`source-${index}`}
              >
                {source.title || source.name || `Source ${index + 1}`}
                {source.similarity && (
                  <span className="ml-1 opacity-75">
                    ({Math.round(source.similarity * 100)}%)
                  </span>
                )}
              </Badge>
            ))}
          </div>
        </div>
      )}

      {/* Action Buttons */}
      <div className="px-4 py-3 border-t border-border bg-muted/5 flex flex-wrap gap-2">
        <Button 
          size="sm" 
          className="inline-flex items-center gap-1.5"
          data-testid="button-generate-draft"
        >
          <FileTextIcon className="w-4 h-4" />
          Generate Draft
        </Button>

        <Button 
          variant="outline" 
          size="sm" 
          className="inline-flex items-center gap-1.5"
          data-testid="button-verify-citations"
        >
          <CheckCircleIcon className="w-4 h-4" />
          Verify Citations
        </Button>

        <Button 
          variant="outline" 
          size="sm" 
          className="inline-flex items-center gap-1.5"
          data-testid="button-save-folder"
        >
          <FolderIcon className="w-4 h-4" />
          Save to Folder
        </Button>

        <Button 
          variant="outline" 
          size="sm" 
          className="inline-flex items-center gap-1.5"
          data-testid="button-export-pdf"
        >
          <DownloadIcon className="w-4 h-4" />
          Export PDF
        </Button>
      </div>
    </Card>
  );
}

server/services/citationService.ts.content:
import { db } from "../db";
import { citationVerifications } from "@shared/schema";
import { eq } from "drizzle-orm";

interface CitationResult {
  citation: string;
  found: boolean;
  source?: string;
  sourceUrl?: string;
  excerpt?: string;
  overruled: boolean;
  confidence: number;
}

export class CitationService {
  async verifyCitations(citations: string[]): Promise<CitationResult[]> {
    const results: CitationResult[] = [];

    for (const citation of citations) {
      try {
        const result = await this.verifySingleCitation(citation);
        results.push(result);
      } catch (error) {
        console.error(`Error verifying citation ${citation}:`, error);
        results.push({
          citation,
          found: false,
          overruled: false,
          confidence: 0,
        });
      }
    }

    return results;
  }

  private async verifySingleCitation(citation: string): Promise<CitationResult> {
    // Check if we have this citation cached
    const [cached] = await db.select()
      .from(citationVerifications)
      .where(eq(citationVerifications.citation, citation))
      .limit(1);

    if (cached) {
      return {
        citation: cached.citation,
        found: cached.found,
        source: cached.source || undefined,
        sourceUrl: cached.sourceUrl || undefined,
        excerpt: cached.excerpt || undefined,
        overruled: cached.overruled || false,
        confidence: cached.confidence || 0,
      };
    }

    // Perform verification (mock implementation for MVP)
    const result = await this.performCitationLookup(citation);

    // Cache the result
    await db.insert(citationVerifications).values({
      citation,
      found: result.found,
      source: result.source,
      sourceUrl: result.sourceUrl,
      excerpt: result.excerpt,
      overruled: result.overruled,
      confidence: result.confidence,
    });

    return result;
  }

  private async performCitationLookup(citation: string): Promise<CitationResult> {
    // Mock citation verification - in production, this would query:
    // 1. SCC/Manupatra APIs (if licensed)
    // 2. IndianKanoon API
    // 3. High Court websites
    // 4. Supreme Court website

    const normalizedCitation = citation.toLowerCase().trim();

    // Mock some known citations for demonstration
    const knownCitations = [
      {
        pattern: /arnesh kumar.*state.*bihar/,
        found: true,
        source: "SCC Online",
        sourceUrl: "https://www.scconline.com/DocumentLink/...",
        excerpt: "The Supreme Court in Arnesh Kumar v. State of Bihar laid down guidelines...",
        overruled: false,
        confidence: 95,
      },
      {
        pattern: /section\s+(41|420)\s+(crpc|ipc)/,
        found: true,
        source: "Legislative Database",
        sourceUrl: "https://legislative.gov.in/...",
        excerpt: "Section 41 of the Code of Criminal Procedure...",
        overruled: false,
        confidence: 100,
      },
      {
        pattern: /\d{4}.*scc.*\d+/,
        found: true,
        source: "SCC",
        sourceUrl: "https://www.supremecourtcases.com/...",
        excerpt: "Supreme Court case reported in SCC...",
        overruled: false,
        confidence: 85,
      }
    ];

    for (const known of knownCitations) {
      if (known.pattern.test(normalizedCitation)) {
        return {
          citation,
          found: known.found,
          source: known.source,
          sourceUrl: known.sourceUrl,
          excerpt: known.excerpt,
          overruled: known.overruled,
          confidence: known.confidence,
        };
      }
    }

    // If not found in known citations, try IndianKanoon search
    return await this.searchIndianKanoon(citation);
  }

  private async searchIndianKanoon(citation: string): Promise<CitationResult> {
    try {
      // Mock IndianKanoon API call
      // In production, this would be: await fetch('https://api.indiankanoon.org/search/...')

      // For now, return a generic "not found" result
      return {
        citation,
        found: false,
        source: "IndianKanoon",
        overruled: false,
        confidence: 0,
      };
    } catch (error) {
      console.error("IndianKanoon search error:", error);
      return {
        citation,
        found: false,
        overruled: false,
        confidence: 0,
      };
    }
  }

  async getVerificationStats(userId: string) {
    // Get verification statistics for user's citations
    const stats = await db.select()
      .from(citationVerifications)
      .where(eq(citationVerifications.found, true));

    const uniqueSources = new Set(stats.map(v => v.source).filter(Boolean));

    return {
      totalVerified: stats.length,
      averageConfidence: stats.reduce((sum, v) => sum + (v.confidence || 0), 0) / stats.length,
      sources: Array.from(uniqueSources) as string[],
    };
  }

  convertIPCtoBNS(ipcSection: string): { bnsSection: string; description: string } | null {
    // IPC to BNS conversion mapping
    const ipcToBnsMap: Record<string, { bns: string; desc: string }> = {
      "420": { bns: "318", desc: "Cheating - IPC Section 420 is now BNS Section 318" },
      "498A": { bns: "85", desc: "Domestic Violence - IPC Section 498A is now BNS Section 85" },
      "302": { bns: "103", desc: "Murder - IPC Section 302 is now BNS Section 103" },
      "376": { bns: "64", desc: "Rape - IPC Section 376 is now BNS Section 64" },
      "325": { bns: "122", desc: "Voluntarily causing grievous hurt" },
      "324": { bns: "118", desc: "Voluntarily causing hurt by dangerous weapons" },
      "323": { bns: "115", desc: "Voluntarily causing hurt" },
      "379": { bns: "303", desc: "Theft" },
      "380": { bns: "305", desc: "Theft in dwelling house" },
    };

    const section = ipcSection.replace(/[^\d]/g, ''); // Extract just the number
    const mapping = ipcToBnsMap[section];

    if (mapping) {
      return {
        bnsSection: mapping.bns,
        description: mapping.desc,
      };
    }

    return null;
  }

  convertBNSToIPC(bnsSection: string): { ipcSection: string; description: string } | null {
    // Reverse mapping
    const bnsToIpcMap: Record<string, { ipc: string; desc: string }> = {
      "318": { ipc: "420", desc: "Cheating - BNS Section 318 was IPC Section 420" },
      "85": { ipc: "498A", desc: "Domestic Violence - BNS Section 85 was IPC Section 498A" },
      "103": { ipc: "302", desc: "Murder - BNS Section 103 was IPC Section 302" },
      "64": { ipc: "376", desc: "Rape - BNS Section 64 was IPC Section 376" },
      "122": { ipc: "325", desc: "Voluntarily causing grievous hurt" },
      "118": { ipc: "324", desc: "Voluntarily causing hurt by dangerous weapons" },
      "115": { ipc: "323", desc: "Voluntarily causing hurt" },
      "303": { ipc: "379", desc: "Theft" },
      "305": { ipc: "380", desc: "Theft in dwelling house" },
    };

    const section = bnsSection.replace(/[^\d]/g, '');
    const mapping = bnsToIpcMap[section];

    if (mapping) {
      return {
        ipcSection: mapping.ipc,
        description: mapping.desc,
      };
    }

    return null;
  }
}

export const citationService = new CitationService();

