import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Card, CardContent } from '@/components/ui/card';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Badge } from '@/components/ui/badge';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { useToast } from '@/hooks/use-toast';
import { isUnauthorizedError } from '@/lib/authUtils';
import {
  RefreshCcwIcon,
  ArrowRightIcon,
  BookOpenIcon,
  AlertCircleIcon,
} from 'lucide-react';

interface ConversionResult {
  bnsSection?: string;
  ipcSection?: string;
  description?: string;
}

interface IPCBNSConverterProps {
  open: boolean;
  onClose: () => void;
}

export default function IPCBNSConverter({ open, onClose }: IPCBNSConverterProps) {
  const [ipcSection, setIpcSection] = useState('');
  const [bnsSection, setBnsSection] = useState('');
  const [ipcResult, setIpcResult] = useState<ConversionResult | null>(null);
  const [bnsResult, setBnsResult] = useState<ConversionResult | null>(null);
  const [isConverting, setIsConverting] = useState(false);
  const { toast } = useToast();

  const handleIPCtoBNS = async () => {
    if (!ipcSection.trim()) {
      toast({
        title: "Input Required",
        description: "Please enter an IPC section number",
        variant: "destructive",
      });
      return;
    }

    try {
      setIsConverting(true);
      const response = await fetch(`/api/citations/convert/ipc-to-bns?section=${encodeURIComponent(ipcSection)}`);

      if (!response.ok) {
        if (response.status === 401) {
          toast({
            title: "Unauthorized",
            description: "You are logged out. Logging in again...",
            variant: "destructive",
          });
          setTimeout(() => {
            window.location.href = "/api/login";
          }, 500);
          return;
        }
        throw new Error('Conversion failed');
      }

      const data = await response.json();

      if (data) {
        setIpcResult(data);
        toast({
          title: "Conversion Complete",
          description: `IPC Section ${ipcSection}  BNS Section ${data.bnsSection}`,
        });
      } else {
        setIpcResult(null);
        toast({
          title: "No Mapping Found",
          description: `No BNS equivalent found for IPC Section ${ipcSection}`,
          variant: "destructive",
        });
      }
    } catch (error: any) {
      toast({
        title: "Conversion Failed",
        description: "Failed to convert IPC to BNS. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsConverting(false);
    }
  };

  const handleBNStoIPC = async () => {
    if (!bnsSection.trim()) {
      toast({
        title: "Input Required",
        description: "Please enter a BNS section number",
        variant: "destructive",
      });
      return;
    }

    try {
      setIsConverting(true);
      const response = await fetch(`/api/citations/convert/bns-to-ipc?section=${encodeURIComponent(bnsSection)}`);

      if (!response.ok) {
        if (response.status === 401) {
          toast({
            title: "Unauthorized",
            description: "You are logged out. Logging in again...",
            variant: "destructive",
          });
          setTimeout(() => {
            window.location.href = "/api/login";
          }, 500);
          return;
        }
        throw new Error('Conversion failed');
      }

      const data = await response.json();

      if (data) {
        setBnsResult(data);
        toast({
          title: "Conversion Complete",
          description: `BNS Section ${bnsSection}  IPC Section ${data.ipcSection}`,
        });
      } else {
        setBnsResult(null);
        toast({
          title: "No Mapping Found",
          description: `No IPC equivalent found for BNS Section ${bnsSection}`,
          variant: "destructive",
        });
      }
    } catch (error: any) {
      toast({
        title: "Conversion Failed",
        description: "Failed to convert BNS to IPC. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsConverting(false);
    }
  };

  return (
    <Dialog open={open} onOpenChange={onClose}>
      <DialogContent className="max-w-3xl" data-testid="ipc-bns-converter-modal">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <RefreshCcwIcon className="w-6 h-6 text-primary" />
            IPC  BNS Converter
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-4">
          <div className="bg-muted/50 p-4 rounded-lg">
            <div className="flex items-start gap-2">
              <AlertCircleIcon className="w-5 h-5 text-blue-500 flex-shrink-0 mt-0.5" />
              <div className="text-sm text-muted-foreground">
                <p className="font-medium text-foreground mb-1">About This Tool</p>
                <p>
                  Convert between the Indian Penal Code (IPC, 1860) and the Bharatiya Nyaya Sanhita (BNS, 2023).
                  The BNS replaces the IPC and came into effect on July 1, 2024.
                </p>
              </div>
            </div>
          </div>

          <Tabs defaultValue="ipc-to-bns" className="w-full">
            <TabsList className="grid w-full grid-cols-2">
              <TabsTrigger value="ipc-to-bns">IPC  BNS</TabsTrigger>
              <TabsTrigger value="bns-to-ipc">BNS  IPC</TabsTrigger>
            </TabsList>

            <TabsContent value="ipc-to-bns" className="space-y-4">
              <div className="space-y-3">
                <div className="space-y-2">
                  <Label htmlFor="ipc-section">IPC Section Number</Label>
                  <div className="flex gap-2">
                    <Input
                      id="ipc-section"
                      placeholder="e.g., 420, 498A, 302"
                      value={ipcSection}
                      onChange={(e) => setIpcSection(e.target.value)}
                      onKeyDown={(e) => e.key === 'Enter' && handleIPCtoBNS()}
                      data-testid="input-ipc-section"
                    />
                    <Button 
                      onClick={handleIPCtoBNS}
                      disabled={isConverting}
                      data-testid="button-convert-ipc-to-bns"
                    >
                      {isConverting ? (
                        <div className="w-4 h-4 animate-spin rounded-full border-2 border-primary-foreground border-t-transparent"></div>
                      ) : (
                        <>
                          Convert
                          <ArrowRightIcon className="w-4 h-4 ml-1" />
                        </>
                      )}
                    </Button>
                  </div>
                  <p className="text-xs text-muted-foreground">
                    Enter the IPC section number (e.g., 420 for Cheating)
                  </p>
                </div>

                {ipcResult && (
                  <Card className="bg-green-50 border-green-200">
                    <CardContent className="p-4">
                      <div className="space-y-3">
                        <div className="flex items-center justify-between">
                          <Badge variant="secondary" className="text-sm">
                            IPC Section {ipcSection}
                          </Badge>
                          <RefreshCcwIcon className="w-4 h-4 text-green-600" />
                          <Badge className="bg-green-600 text-white text-sm">
                            BNS Section {ipcResult.bnsSection}
                          </Badge>
                        </div>
                        <div>
                          <p className="text-sm font-medium text-green-900 mb-1">Description:</p>
                          <p className="text-sm text-green-800">{ipcResult.description}</p>
                        </div>
                        <div className="flex items-center gap-2 text-xs text-green-700">
                          <BookOpenIcon className="w-3 h-3" />
                          <span>Effective from July 1, 2024</span>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                )}
              </div>
            </TabsContent>

            <TabsContent value="bns-to-ipc" className="space-y-4">
              <div className="space-y-3">
                <div className="space-y-2">
                  <Label htmlFor="bns-section">BNS Section Number</Label>
                  <div className="flex gap-2">
                    <Input
                      id="bns-section"
                      placeholder="e.g., 318, 85, 103"
                      value={bnsSection}
                      onChange={(e) => setBnsSection(e.target.value)}
                      onKeyDown={(e) => e.key === 'Enter' && handleBNStoIPC()}
                      data-testid="input-bns-section"
                    />
                    <Button 
                      onClick={handleBNStoIPC}
                      disabled={isConverting}
                      data-testid="button-convert-bns-to-ipc"
                    >
                      {isConverting ? (
                        <div className="w-4 h-4 animate-spin rounded-full border-2 border-primary-foreground border-t-transparent"></div>
                      ) : (
                        <>
                          Convert
                          <ArrowRightIcon className="w-4 h-4 ml-1" />
                        </>
                      )}
                    </Button>
                  </div>
                  <p className="text-xs text-muted-foreground">
                    Enter the BNS section number (e.g., 318 for Cheating)
                  </p>
                </div>

                {bnsResult && (
                  <Card className="bg-blue-50 border-blue-200">
                    <CardContent className="p-4">
                      <div className="space-y-3">
                        <div className="flex items-center justify-between">
                          <Badge className="bg-blue-600 text-white text-sm">
                            BNS Section {bnsSection}
                          </Badge>
                          <RefreshCcwIcon className="w-4 h-4 text-blue-600" />
                          <Badge variant="secondary" className="text-sm">
                            IPC Section {bnsResult.ipcSection}
                          </Badge>
                        </div>
                        <div>
                          <p className="text-sm font-medium text-blue-900 mb-1">Description:</p>
                          <p className="text-sm text-blue-800">{bnsResult.description}</p>
                        </div>
                        <div className="flex items-center gap-2 text-xs text-blue-700">
                          <BookOpenIcon className="w-3 h-3" />
                          <span>Legacy IPC provision (pre-July 2024)</span>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                )}
              </div>
            </TabsContent>
          </Tabs>

          <div className="pt-4 border-t">
            <Button variant="outline" onClick={onClose} className="w-full" data-testid="button-close-converter">
              Close
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/Tools/CitationVeri.content:
import { useState } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Textarea } from '@/components/ui/textarea';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { ScrollArea } from '@/components/ui/scroll-area';
import { Separator } from '@/components/ui/separator';
import { useToast } from '@/hooks/use-toast';
import { 
  CheckCircleIcon, 
  XCircleIcon, 
  AlertTriangleIcon, 
  ExternalLinkIcon,
  LoaderIcon,
  InfoIcon
} from 'lucide-react';

interface CitationVerifierProps {
  open: boolean;
  onClose: () => void;
}

interface CitationResult {
  citation: string;
  found: boolean;
  source?: string;
  sourceUrl?: string;
  excerpt?: string;
  overruled: boolean;
  confidence: number;
}

export default function CitationVerifier({ open, onClose }: CitationVerifierProps) {
  const [citationText, setCitationText] = useState('');
  const [isVerifying, setIsVerifying] = useState(false);
  const [results, setResults] = useState<CitationResult[]>([]);
  const { toast } = useToast();

  const handleVerify = async () => {
    if (!citationText.trim()) {
      toast({
        title: "Input Required",
        description: "Please enter at least one citation to verify",
        variant: "destructive",
      });
      return;
    }

    try {
      setIsVerifying(true);

      const citations = citationText
        .split('\n')
        .map(line => line.trim())
        .filter(line => line.length > 0);

      const response = await fetch('/api/citations/verify', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ citations }),
      });

      if (!response.ok) {
        if (response.status === 401) {
          toast({
            title: "Unauthorized",
            description: "You are logged out. Logging in again...",
            variant: "destructive",
          });
          setTimeout(() => {
            window.location.href = "/api/login";
          }, 500);
          return;
        }
        throw new Error('Verification failed');
      }

      const data = await response.json();
      setResults(data);

      const foundCount = data.filter((r: CitationResult) => r.found).length;
      toast({
        title: "Verification Complete",
        description: `Verified ${citations.length} citation(s). ${foundCount} found.`,
      });
    } catch (error: any) {
      toast({
        title: "Verification Failed",
        description: "Failed to verify citations. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsVerifying(false);
    }
  };

  const handleClear = () => {
    setCitationText('');
    setResults([]);
  };

  const getConfidenceBadge = (confidence: number) => {
    if (confidence >= 80) {
      return <Badge className="bg-green-500/10 text-green-700 dark:text-green-400">High Confidence</Badge>;
    } else if (confidence >= 50) {
      return <Badge className="bg-yellow-500/10 text-yellow-700 dark:text-yellow-400">Medium Confidence</Badge>;
    } else {
      return <Badge className="bg-red-500/10 text-red-700 dark:text-red-400">Low Confidence</Badge>;
    }
  };

  return (
    <Dialog open={open} onOpenChange={onClose}>
      <DialogContent className="max-w-4xl max-h-[90vh] flex flex-col" data-testid="citation-verifier-modal">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <CheckCircleIcon className="w-5 h-5" />
            Citation Verifier
          </DialogTitle>
        </DialogHeader>

        <div className="flex-1 flex flex-col gap-4 overflow-hidden">
          {/* Input Section */}
          <div className="space-y-2">
            <label className="text-sm font-medium">Citations (one per line)</label>
            <Textarea
              placeholder="Enter citations to verify, one per line. Examples:&#10;Arnesh Kumar v. State of Bihar&#10;Section 420 IPC&#10;2014 SCC 556"
              value={citationText}
              onChange={(e) => setCitationText(e.target.value)}
              className="min-h-[120px] font-mono text-sm"
              data-testid="input-citations"
            />
            <p className="text-xs text-muted-foreground flex items-center gap-1">
              <InfoIcon className="w-3 h-3" />
              Enter case names, section references, or SCC citations
            </p>
          </div>

          {/* Action Buttons */}
          <div className="flex gap-2">
            <Button 
              onClick={handleVerify} 
              disabled={isVerifying || !citationText.trim()}
              className="flex-1"
              data-testid="button-verify"
            >
              {isVerifying ? (
                <>
                  <LoaderIcon className="w-4 h-4 mr-2 animate-spin" />
                  Verifying...
                </>
              ) : (
                <>
                  <CheckCircleIcon className="w-4 h-4 mr-2" />
                  Verify Citations
                </>
              )}
            </Button>
            <Button 
              variant="outline" 
              onClick={handleClear}
              disabled={isVerifying}
              data-testid="button-clear"
            >
              Clear
            </Button>
          </div>

          {/* Results Section */}
          {results.length > 0 && (
            <>
              <Separator />
              <div className="flex-1 overflow-hidden flex flex-col">
                <h3 className="text-sm font-medium mb-2">Verification Results ({results.length})</h3>
                <ScrollArea className="flex-1">
                  <div className="space-y-3 pr-4">
                    {results.map((result, index) => (
                      <Card 
                        key={index} 
                        className={`${
                          result.found 
                            ? 'border-green-200 dark:border-green-800/50 bg-green-50/50 dark:bg-green-950/20' 
                            : 'border-red-200 dark:border-red-800/50 bg-red-50/50 dark:bg-red-950/20'
                        }`}
                        data-testid={`result-citation-${index}`}
                      >
                        <CardHeader className="pb-3">
                          <CardTitle className="text-sm font-mono flex items-start justify-between gap-2">
                            <span className="flex-1">{result.citation}</span>
                            <div className="flex items-center gap-2">
                              {result.overruled && (
                                <Badge variant="destructive" className="text-xs">
                                  <AlertTriangleIcon className="w-3 h-3 mr-1" />
                                  Overruled
                                </Badge>
                              )}
                              {result.found ? (
                                <CheckCircleIcon className="w-5 h-5 text-green-600 dark:text-green-400 flex-shrink-0" />
                              ) : (
                                <XCircleIcon className="w-5 h-5 text-red-600 dark:text-red-400 flex-shrink-0" />
                              )}
                            </div>
                          </CardTitle>
                        </CardHeader>
                        <CardContent className="space-y-2">
                          {result.found ? (
                            <>
                              <div className="flex items-center gap-2 flex-wrap">
                                <Badge variant="secondary" className="text-xs">
                                  {result.source || 'Unknown Source'}
                                </Badge>
                                {getConfidenceBadge(result.confidence)}
                                {result.sourceUrl && (
                                  <a
                                    href={result.sourceUrl}
                                    target="_blank"
                                    rel="noopener noreferrer"
                                    className="text-xs text-blue-600 dark:text-blue-400 hover:underline flex items-center gap-1"
                                  >
                                    View Source
                                    <ExternalLinkIcon className="w-3 h-3" />
                                  </a>
                                )}
                              </div>
                              {result.excerpt && (
                                <p className="text-xs text-muted-foreground italic border-l-2 border-muted pl-2">
                                  {result.excerpt}
                                </p>
                              )}
                            </>
                          ) : (
                            <p className="text-xs text-muted-foreground">
                              Citation not found in available databases. Please verify manually.
                            </p>
                          )}
                        </CardContent>
                      </Card>
                    ))}
                  </div>
                </ScrollArea>
              </div>
            </>
          )}

          {/* Close Button */}
          <div className="flex justify-end pt-2">
            <Button 
              variant="outline" 
              onClick={onClose}
              data-testid="button-close-verifier"
            >
              Close
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/Tools/BareActExplo.content:
import { useState } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { ScrollArea } from '@/components/ui/scroll-area';
import { Separator } from '@/components/ui/separator';
import { useToast } from '@/hooks/use-toast';
import { 
  BookOpenIcon, 
  SearchIcon, 
  ExternalLinkIcon,
  LoaderIcon,
  InfoIcon,
  FileTextIcon
} from 'lucide-react';

interface BareActExplorerProps {
  open: boolean;
  onClose: () => void;
}

interface StatuteResult {
  title: string;
  sections: string[];
  url: string;
}

export default function BareActExplorer({ open, onClose }: BareActExplorerProps) {
  const [searchQuery, setSearchQuery] = useState('');
  const [isSearching, setIsSearching] = useState(false);
  const [results, setResults] = useState<StatuteResult[]>([]);
  const { toast } = useToast();

  const handleSearch = async (query?: string) => {
    const searchTerm = query || searchQuery;

    if (!searchTerm.trim()) {
      toast({
        title: "Input Required",
        description: "Please enter a search term",
        variant: "destructive",
      });
      return;
    }

    // Update search query if passed directly
    if (query) {
      setSearchQuery(query);
    }

    try {
      setIsSearching(true);

      const response = await fetch(`/api/search/statutes?q=${encodeURIComponent(searchTerm)}`);

      if (!response.ok) {
        if (response.status === 401) {
          toast({
            title: "Unauthorized",
            description: "You are logged out. Logging in again...",
            variant: "destructive",
          });
          setTimeout(() => {
            window.location.href = "/api/login";
          }, 500);
          return;
        }
        throw new Error('Search failed');
      }

      const data = await response.json();
      setResults(data);

      toast({
        title: "Search Complete",
        description: `Found ${data.length} statute(s) matching "${searchTerm}"`,
      });
    } catch (error: any) {
      toast({
        title: "Search Failed",
        description: "Failed to search statutes. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsSearching(false);
    }
  };

  const handleClear = () => {
    setSearchQuery('');
    setResults([]);
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !isSearching) {
      handleSearch();  // No parameter, uses state
    }
  };

  return (
    <Dialog open={open} onOpenChange={onClose}>
      <DialogContent className="max-w-4xl max-h-[90vh] flex flex-col" data-testid="bare-act-explorer-modal">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <BookOpenIcon className="w-5 h-5" />
            Bare Act Explorer
          </DialogTitle>
        </DialogHeader>

        <div className="flex-1 flex flex-col gap-4 overflow-hidden">
          {/* Search Section */}
          <div className="space-y-2">
            <label className="text-sm font-medium">Search Statutes</label>
            <div className="flex gap-2">
              <Input
                placeholder="Search by statute name or section (e.g., 'IPC 420', 'CrPC', 'BNS')"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                onKeyPress={handleKeyPress}
                className="flex-1"
                data-testid="input-search-statute"
              />
              <Button 
                onClick={() => handleSearch()} 
                disabled={isSearching || !searchQuery.trim()}
                data-testid="button-search-statute"
              >
                {isSearching ? (
                  <>
                    <LoaderIcon className="w-4 h-4 mr-2 animate-spin" />
                    Searching...
                  </>
                ) : (
                  <>
                    <SearchIcon className="w-4 h-4 mr-2" />
                    Search
                  </>
                )}
              </Button>
            </div>
            <p className="text-xs text-muted-foreground flex items-center gap-1">
              <InfoIcon className="w-3 h-3" />
              Search for Indian Penal Code (IPC), CrPC, BNS, or specific sections
            </p>
          </div>

          {/* Quick Search Suggestions */}
          <div className="flex gap-2 flex-wrap">
            <Button 
              variant="outline" 
              size="sm"
              onClick={() => handleSearch('IPC')}
              disabled={isSearching}
            >
              IPC
            </Button>
            <Button 
              variant="outline" 
              size="sm"
              onClick={() => handleSearch('CrPC')}
              disabled={isSearching}
            >
              CrPC
            </Button>
            <Button 
              variant="outline" 
              size="sm"
              onClick={() => handleSearch('BNS')}
              disabled={isSearching}
            >
              BNS
            </Button>
            <Button 
              variant="outline" 
              size="sm"
              onClick={() => handleSearch('420')}
              disabled={isSearching}
            >
              Section 420
            </Button>
          </div>

          {/* Results Section */}
          {results.length > 0 && (
            <>
              <Separator />
              <div className="flex-1 overflow-hidden flex flex-col">
                <div className="flex items-center justify-between mb-2">
                  <h3 className="text-sm font-medium">Results ({results.length})</h3>
                  <Button 
                    variant="ghost" 
                    size="sm"
                    onClick={handleClear}
                    data-testid="button-clear-results"
                  >
                    Clear
                  </Button>
                </div>
                <ScrollArea className="flex-1">
                  <div className="space-y-3 pr-4">
                    {results.map((statute, index) => (
                      <Card 
                        key={index} 
                        className="border-blue-200 dark:border-blue-800/50 bg-blue-50/50 dark:bg-blue-950/20"
                        data-testid={`result-statute-${index}`}
                      >
                        <CardHeader className="pb-3">
                          <CardTitle className="text-base flex items-start justify-between gap-2">
                            <div className="flex items-start gap-2 flex-1">
                              <FileTextIcon className="w-5 h-5 text-blue-600 dark:text-blue-400 mt-0.5 flex-shrink-0" />
                              <span>{statute.title}</span>
                            </div>
                            <Badge variant="secondary" className="text-xs whitespace-nowrap">
                              {statute.sections.length} section{statute.sections.length !== 1 ? 's' : ''}
                            </Badge>
                          </CardTitle>
                        </CardHeader>
                        <CardContent className="space-y-3">
                          {/* Sections */}
                          <div>
                            <p className="text-xs font-medium text-muted-foreground mb-2">Relevant Sections:</p>
                            <div className="space-y-1">
                              {statute.sections.map((section, sectionIndex) => (
                                <div 
                                  key={sectionIndex}
                                  className="text-sm pl-3 border-l-2 border-blue-300 dark:border-blue-700 py-1"
                                >
                                  {section}
                                </div>
                              ))}
                            </div>
                          </div>

                          {/* View Full Act Link */}
                          {statute.url && (
                            <div className="pt-2">
                              <a
                                href={statute.url}
                                target="_blank"
                                rel="noopener noreferrer"
                                className="inline-flex items-center gap-1 text-sm text-blue-600 dark:text-blue-400 hover:underline"
                                data-testid={`link-statute-${index}`}
                              >
                                <ExternalLinkIcon className="w-3 h-3" />
                                View Full Bare Act
                              </a>
                            </div>
                          )}
                        </CardContent>
                      </Card>
                    ))}
                  </div>
                </ScrollArea>
              </div>
            </>
          )}

          {/* No Results Message */}
          {results.length === 0 && searchQuery && !isSearching && (
            <div className="text-center py-8 text-muted-foreground">
              <BookOpenIcon className="w-12 h-12 mx-auto mb-3 opacity-20" />
              <p className="text-sm">No statutes found matching "{searchQuery}"</p>
              <p className="text-xs mt-1">Try searching for IPC, CrPC, BNS, or specific section numbers</p>
            </div>
          )}

          {/* Initial State Message */}
          {results.length === 0 && !searchQuery && !isSearching && (
            <div className="text-center py-8 text-muted-foreground">
              <BookOpenIcon className="w-12 h-12 mx-auto mb-3 opacity-20" />
              <p className="text-sm">Search for Indian statutes</p>
              <p className="text-xs mt-1">Use the quick search buttons or enter your own query</p>
            </div>
          )}

          {/* Close Button */}
          <div className="flex justify-end pt-2">
            <Button 
              variant="outline" 
              onClick={onClose}
              data-testid="button-close-explorer"
            >
              Close
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

server/services/analysisPrompts.ts.content:
// LLM Prompt Templates for Judgment Analysis
// Each template includes: system instructions, user prompt, and expected output format

export interface AnalysisPrompt {
  step: string;
  systemPrompt: string;
  userPromptTemplate: string;
  outputFormat: string;
  requiredFields: string[];
}

export const analysisPrompts: Record<string, AnalysisPrompt> = {
  // Step 1: Metadata Extraction
  metadata: {
    step: 'metadata',
    systemPrompt: `You are a legal metadata extraction specialist for Indian court judgments. Extract structured case information from the judgment text with precise citations to paragraph/page numbers.`,
    userPromptTemplate: `Extract the following metadata from this judgment:

JUDGMENT TEXT:
{judgmentChunks}

Extract and return JSON with:
1. caseName: Full case title (e.g., "State of Punjab v. Ajaib Singh")
2. citation: Primary citation (e.g., "AIR 1953 SC 10")
3. court: Court name (e.g., "Supreme Court of India", "Delhi High Court")
4. bench: Bench composition (e.g., "Division Bench of 2 judges", "Constitutional Bench of 5 judges")
5. judges: Array of judge names
6. date: Decision date (YYYY-MM-DD format)
7. anchors: Object mapping each field to {page, paragraph} citation

IMPORTANT: Include precise anchors (page and paragraph numbers) for each extracted field.`,
    outputFormat: 'JSON',
    requiredFields: ['caseName', 'citation', 'court', 'bench', 'judges', 'date', 'anchors']
  },

  // Step 2: Facts Extraction
  facts: {
    step: 'facts',
    systemPrompt: `You are a legal facts extraction specialist. Extract only the factual background of the case, distinguishing between admitted facts and disputed facts. Provide precise paragraph/page citations.`,
    userPromptTemplate: `Extract the factual background from this judgment:

JUDGMENT TEXT:
{judgmentChunks}

METADATA CONTEXT:
{metadata}

Extract and return JSON with:
1. admittedFacts: Array of undisputed facts, each with {text, anchor: {page, paragraph}}
2. disputedFacts: Array of contested facts, each with {text, anchor: {page, paragraph}}
3. proceduralHistory: Array of procedural events (lower court proceedings, appeals), each with {text, anchor: {page, paragraph}}
4. parties: Object with {appellant: {name, role, anchor}, respondent: {name, role, anchor}, intervenors: [{name, role, anchor}]}

Anchor each fact AND party information to specific paragraph and page numbers.`,
    outputFormat: 'JSON',
    requiredFields: ['admittedFacts', 'disputedFacts', 'proceduralHistory', 'parties']
  },

  // Step 3: Timeline Generation
  timeline: {
    step: 'timeline',
    systemPrompt: `You are a legal timeline specialist. Create a chronological timeline of events from the judgment, including both factual events and procedural milestones. Each event must have a date and citation.`,
    userPromptTemplate: `Generate a chronological timeline from this judgment:

JUDGMENT TEXT:
{judgmentChunks}

FACTS CONTEXT:
{facts}

Extract and return JSON with:
1. events: Array of chronological events, each with:
   - date: Event date (YYYY-MM-DD, or "Unknown" if not specified)
   - description: Brief event description
   - type: "factual" | "procedural" | "legal"
   - anchor: {page, paragraph}

Sort events chronologically. Include both factual events and court proceedings.`,
    outputFormat: 'JSON',
    requiredFields: ['events']
  },

  // Step 4: Issues Identification
  issues: {
    step: 'issues',
    systemPrompt: `You are a legal issues identification specialist. Extract the key legal questions and issues framed by the court. Distinguish between main issues and subsidiary issues.`,
    userPromptTemplate: `Identify the legal issues from this judgment:

JUDGMENT TEXT:
{judgmentChunks}

FACTS CONTEXT:
{facts}

Extract and return JSON with:
1. mainIssues: Array of primary legal questions, each with:
   - issueNumber: Sequential number
   - question: The legal question posed
   - anchor: {page, paragraph}
2. subsidiaryIssues: Array of secondary/derivative issues
3. issuesFraming: Verbatim text of how court framed the issues with anchor

Focus on questions of law, not facts.`,
    outputFormat: 'JSON',
    requiredFields: ['mainIssues', 'subsidiaryIssues', 'issuesFraming']
  },

  // Step 5: Arguments Analysis
  arguments: {
    step: 'arguments',
    systemPrompt: `You are a legal arguments analysis specialist. Extract and organize arguments presented by each party, along with court's consideration of those arguments.`,
    userPromptTemplate: `Analyze the arguments from this judgment:

JUDGMENT TEXT:
{judgmentChunks}

ISSUES CONTEXT:
{issues}

Extract and return JSON with:
1. appellantArguments: Array of arguments by appellant/petitioner, each with:
   - summary: Argument summary
   - supportingAuthorities: Array of cases/statutes cited
   - anchor: {page, paragraph}
2. respondentArguments: Array of arguments by respondent
3. courtAnalysis: Array of court's consideration/response to each argument, each with:
   - analysis: Court's response/reasoning
   - relatedArgument: Which argument this addresses
   - anchor: {page, paragraph}

Link arguments to specific issues where applicable.`,
    outputFormat: 'JSON',
    requiredFields: ['appellantArguments', 'respondentArguments', 'courtAnalysis']
  },

  // Step 6: Ratio Decidendi (Binding Law)
  ratio: {
    step: 'ratio',
    systemPrompt: `You are a legal ratio decidendi extraction specialist. Identify the binding legal principles that form the basis of the decision. Ratio decidendi is the legal reasoning necessary for the decision.`,
    userPromptTemplate: `Extract the ratio decidendi from this judgment:

JUDGMENT TEXT:
{judgmentChunks}

ISSUES CONTEXT:
{issues}

Extract and return JSON with:
1. ratioStatements: Array of binding legal propositions, each with:
   - principle: The legal principle established
   - relatedIssue: Which issue this ratio addresses
   - anchor: {page, paragraph}
   - verbatimQuote: Exact quote from judgment
2. holdings: Final decision/verdict on each issue, each with {decision, relatedIssue, anchor: {page, paragraph}}
3. legalTests: Any tests or standards established by the court, each with {testName, description, anchor: {page, paragraph}}

Ratio must be necessary for the decision, not merely persuasive observations.`,
    outputFormat: 'JSON',
    requiredFields: ['ratioStatements', 'holdings', 'legalTests']
  },

  // Step 7: Obiter Dicta (Non-binding Observations)
  obiter: {
    step: 'obiter',
    systemPrompt: `You are a legal obiter dicta extraction specialist. Identify observations, remarks, and legal discussions that are NOT essential to the decision but may be persuasive.`,
    userPromptTemplate: `Extract obiter dicta from this judgment:

JUDGMENT TEXT:
{judgmentChunks}

RATIO CONTEXT:
{ratio}

Extract and return JSON with:
1. obiterStatements: Array of non-binding observations, each with:
   - observation: The court's remark/observation
   - context: Why this was discussed (though not necessary for decision)
   - anchor: {page, paragraph}
   - verbatimQuote: Exact quote
2. dicta: General legal discussions or commentary, each with {discussion, anchor: {page, paragraph}}
3. hypotheticals: Any hypothetical scenarios discussed, each with {scenario, anchor: {page, paragraph}}

Clearly distinguish from ratio - these are NOT binding.`,
    outputFormat: 'JSON',
    requiredFields: ['obiterStatements', 'dicta', 'hypotheticals']
  },

  // Step 8: Statutes & Provisions
  statutes: {
    step: 'statutes',
    systemPrompt: `You are a legal statutes and provisions extraction specialist. Extract all statutes, sections, articles, and legal provisions cited in the judgment. For IPC provisions, provide BNS equivalents.`,
    userPromptTemplate: `Extract statutes and provisions from this judgment:

JUDGMENT TEXT:
{judgmentChunks}

Extract and return JSON with:
1. statutes: Array of statutes/acts cited, each with:
   - name: Full statute name (e.g., "Indian Penal Code, 1860")
   - sections: Array of specific sections/articles cited
   - anchor: {page, paragraph} where first cited
2. ipcToBns: For IPC provisions, map to BNS equivalents:
   - ipcSection: Old IPC section
   - bnsSection: Corresponding BNS section
   - description: What the provision addresses
3. constitutionalProvisions: Separate array for Constitution articles
4. interpretationNotes: How the court interpreted each provision, each with {statute, section, interpretation, anchor: {page, paragraph}}

Include full citation format for each statute.`,
    outputFormat: 'JSON',
    requiredFields: ['statutes', 'ipcToBns', 'constitutionalProvisions', 'interpretationNotes']
  },

  // Step 9: Precedents Cited
  precedents: {
    step: 'precedents',
    systemPrompt: `You are a legal precedents extraction specialist. Extract all case law citations, categorize by treatment (followed, distinguished, overruled), and provide precise anchors.`,
    userPromptTemplate: `Extract precedents cited from this judgment:

JUDGMENT TEXT:
{judgmentChunks}

Extract and return JSON with:
1. precedents: Array of cases cited, each with:
   - caseName: Case title
   - citation: Full citation (e.g., "AIR 1973 SC 1461")
   - treatment: "followed" | "distinguished" | "overruled" | "referred" | "approved"
   - proposition: Legal proposition for which case was cited
   - anchor: {page, paragraph}
2. bindingPrecedents: Supreme Court cases that bound the court, each with {caseName, citation, proposition, anchor: {page, paragraph}}
3. persuasivePrecedents: High Court or foreign cases cited persuasively, each with {caseName, citation, proposition, anchor: {page, paragraph}}
4. overruledCases: Any precedents explicitly overruled, each with {caseName, citation, reason, anchor: {page, paragraph}}

Verify citations are accurate and complete.`,
    outputFormat: 'JSON',
    requiredFields: ['precedents', 'bindingPrecedents', 'persuasivePrecedents', 'overruledCases']
  },

  // Step 10: Executive Summary
  summary: {
    step: 'summary',
    systemPrompt: `You are a legal summary specialist. Create a comprehensive yet concise executive summary of the judgment suitable for lawyers and law students.`,
    userPromptTemplate: `Generate an executive summary from this judgment:

JUDGMENT TEXT:
{judgmentChunks}

ANALYSIS CONTEXT:
Metadata: {metadata}
Facts: {facts}
Timeline: {timeline}
Issues: {issues}
Arguments: {arguments}
Ratio: {ratio}
Obiter: {obiter}
Statutes: {statutes}
Precedents: {precedents}

Generate and return JSON with:
1. headnote: One-paragraph summary (150-200 words) covering facts, issues, and decision
2. keyTakeaways: Array of 3-5 bullet points with main legal principles
3. practicalImplications: How this judgment affects legal practice
4. relatedAreas: Legal domains this judgment impacts (e.g., "Criminal Law", "Evidence Law")
5. verdict: Final outcome (e.g., "Appeal allowed", "Writ dismissed")
6. anchors: Citations to key paragraphs supporting the summary

Make it accessible but legally accurate.`,
    outputFormat: 'JSON',
    requiredFields: ['headnote', 'keyTakeaways', 'practicalImplications', 'relatedAreas', 'verdict', 'anchors']
  }
};

// Helper function to generate prompt with context
export function generateAnalysisPrompt(
  step: string,
  judgmentChunks: string,
  context: Record<string, any> = {}
): { system: string; user: string; outputFormat: string } {
  const template = analysisPrompts[step];

  if (!template) {
    throw new Error(`Unknown analysis step: ${step}`);
  }

  // Replace placeholders in user prompt
  let userPrompt = template.userPromptTemplate.replace('{judgmentChunks}', judgmentChunks);

  // Replace context placeholders (using replaceAll to avoid regex escaping issues)
  for (const [key, value] of Object.entries(context)) {
    const placeholder = `{${key}}`;
    const contextValue = typeof value === 'string' ? value : JSON.stringify(value, null, 2);
    userPrompt = userPrompt.replaceAll(placeholder, contextValue);
  }

  return {
    system: template.systemPrompt,
    user: userPrompt,
    outputFormat: template.outputFormat
  };
}

// Analysis step execution order
export const analysisStepOrder = [
  'metadata',
  'facts',
  'timeline',
  'issues',
  'arguments',
  'ratio',
  'obiter',
  'statutes',
  'precedents',
  'summary'
];

