import { openai } from '../openai';
import { storage } from '../storage';
import { pdfExtractionService, type ExtractionResult } from './pdfExtractionService';
import { chunkingService, type JudgmentChunk } from './chunkingService';
import { analysisPrompts, generateAnalysisPrompt, analysisStepOrder } from './analysisPrompts';

export interface AnalysisStepResult {
  step: string;
  result: any;
  anchors: any[];
  model: string;
  tokensUsed: number;
  timestamp: string;
}

export interface CompleteAnalysisResult {
  steps: Record<string, AnalysisStepResult>;
  summary: any;
  totalTokensUsed: number;
  completedAt: string;
}

class AnalysisService {
  private readonly MAX_CHUNKS_PER_STEP = 20; // Limit chunks to avoid token overflow
  private readonly MODEL = 'openai/gpt-4o-mini'; // Use GPT-4 Omni Mini for cost efficiency

  async analyzeJudgment(
    judgmentId: string,
    extractionResult: ExtractionResult,
    chunks: JudgmentChunk[]
  ): Promise<CompleteAnalysisResult> {
    const stepResults: Record<string, AnalysisStepResult> = {};
    const stepContext: Record<string, any> = {};
    let totalTokens = 0;

    console.log(`Starting multi-step analysis for judgment ${judgmentId} with ${chunks.length} chunks`);

    // Execute each analysis step in sequence
    for (const step of analysisStepOrder) {
      try {
        console.log(`Executing analysis step: ${step}`);

        // Select relevant chunks for this step (simplified - in production, use semantic search)
        const relevantChunks = this.selectRelevantChunks(chunks, step);
        const chunksText = relevantChunks.map(c => c.text).join('\n\n');

        // Generate prompt with context from previous steps
        const { system, user, outputFormat } = generateAnalysisPrompt(
          step,
          chunksText,
          stepContext
        );

        // Call LLM with structured output
        const response = await this.callLLMWithRetry(system, user, step);

        // Parse and validate response
        const parsedResult = this.parseAndValidateResponse(response.content, step);

        // Extract anchors from result
        const anchors = this.extractAnchors(parsedResult);

        // Store step result
        stepResults[step] = {
          step,
          result: parsedResult,
          anchors,
          model: this.MODEL,
          tokensUsed: response.usage?.total_tokens || 0,
          timestamp: new Date().toISOString(),
        };

        // Add this step's result to context for next steps
        stepContext[step] = parsedResult;
        totalTokens += response.usage?.total_tokens || 0;

        console.log(`Completed step ${step}: ${anchors.length} anchors extracted, ${response.usage?.total_tokens} tokens used`);

      } catch (error) {
        console.error(`Failed to execute step ${step}:`, error);
        throw new Error(`Analysis step '${step}' failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
      }
    }

    return {
      steps: stepResults,
      summary: stepResults.summary?.result || null,
      totalTokensUsed: totalTokens,
      completedAt: new Date().toISOString(),
    };
  }

  private selectRelevantChunks(chunks: JudgmentChunk[], step: string): JudgmentChunk[] {
    // Simplified selection: Take first N chunks
    // In production: Use vector similarity search based on step type

    // For metadata/summary, prefer early chunks (headers, case details)
    if (step === 'metadata' || step === 'summary') {
      return chunks.slice(0, this.MAX_CHUNKS_PER_STEP);
    }

    // For facts/timeline, prefer middle chunks (narrative)
    if (step === 'facts' || step === 'timeline') {
      const start = Math.floor(chunks.length * 0.2);
      return chunks.slice(start, start + this.MAX_CHUNKS_PER_STEP);
    }

    // For ratio/obiter/precedents, prefer later chunks (reasoning, conclusion)
    if (step === 'ratio' || step === 'obiter' || step === 'precedents') {
      const start = Math.max(0, chunks.length - this.MAX_CHUNKS_PER_STEP);
      return chunks.slice(start);
    }

    // Default: take middle chunks
    const start = Math.max(0, Math.floor((chunks.length - this.MAX_CHUNKS_PER_STEP) / 2));
    return chunks.slice(start, start + this.MAX_CHUNKS_PER_STEP);
  }

  private async callLLMWithRetry(
    systemPrompt: string,
    userPrompt: string,
    step: string,
    retries = 2
  ): Promise<any> {
    for (let attempt = 0; attempt <= retries; attempt++) {
      try {
        const response = await openai.chat.completions.create({
          model: this.MODEL,
          messages: [
            { role: 'system', content: systemPrompt },
            { role: 'user', content: userPrompt }
          ],
          response_format: { type: 'json_object' },
          temperature: 0.3, // Lower temperature for more consistent outputs
          max_tokens: 4000,
        });

        return response.choices[0].message;
      } catch (error) {
        if (attempt === retries) {
          throw error;
        }
        console.warn(`LLM call failed for step ${step}, attempt ${attempt + 1}/${retries + 1}:`, error);
        // Wait before retry (exponential backoff)
        await new Promise(resolve => setTimeout(resolve, Math.pow(2, attempt) * 1000));
      }
    }
  }

  private parseAndValidateResponse(content: string | null | undefined, step: string): any {
    if (!content) {
      throw new Error(`No content in LLM response for step ${step}`);
    }

    try {
      const parsed = JSON.parse(content);

      // Validate required fields exist
      const template = analysisPrompts[step];
      if (template) {
        for (const field of template.requiredFields) {
          if (!(field in parsed)) {
            console.warn(`Missing required field '${field}' in step ${step}`);
          }
        }
      }

      return parsed;
    } catch (error) {
      throw new Error(`Failed to parse JSON response for step ${step}: ${error instanceof Error ? error.message : 'Invalid JSON'}`);
    }
  }

  private extractAnchors(result: any): any[] {
    const anchors: any[] = [];

    const extractFromValue = (value: any, path: string = '') => {
      if (value && typeof value === 'object') {
        if ('anchor' in value && value.anchor) {
          anchors.push({
            path,
            anchor: value.anchor
          });
        }

        if (Array.isArray(value)) {
          value.forEach((item, index) => extractFromValue(item, `${path}[${index}]`));
        } else {
          Object.entries(value).forEach(([key, val]) => {
            extractFromValue(val, path ? `${path}.${key}` : key);
          });
        }
      }
    };

    extractFromValue(result);
    return anchors;
  }

  async runFullAnalysis(
    judgmentId: string, 
    analysisId: string,
    extractionResult?: ExtractionResult,
    chunks?: JudgmentChunk[]
  ): Promise<void> {
    try {
      console.log(`Starting full analysis for judgment ${judgmentId}, analysis ${analysisId}`);

      // If extraction result and chunks not provided, extract and chunk
      if (!extractionResult || !chunks) {
        const judgment = await storage.getJudgment(judgmentId);
        if (!judgment || !judgment.s3Path) {
          throw new Error(`Judgment ${judgmentId} not found or has no file`);
        }

        // Extract text from PDF
        console.log(`Extracting text from ${judgment.s3Path}`);
        extractionResult = await pdfExtractionService.extractFromPath(judgment.s3Path);

        // Chunk the text
        console.log(`Chunking ${extractionResult.pages.length} pages`);
        const chunkingResult = await chunkingService.chunkPages(extractionResult.pages, judgmentId);
        chunks = chunkingResult.chunks;
      }

      // Run analysis pipeline
      const analysisResult = await this.analyzeJudgment(
        judgmentId,
        extractionResult,
        chunks
      );

      // Update analysis in database
      await storage.updateAnalysis(analysisId, {
        status: 'completed',
        result: analysisResult,
        error: null,
      });

      console.log(`Analysis ${analysisId} completed successfully. Total tokens: ${analysisResult.totalTokensUsed}`);

    } catch (error) {
      console.error(`Analysis failed for judgment ${judgmentId}:`, error);

      // Update analysis status to failed
      await storage.updateAnalysis(analysisId, {
        status: 'failed',
        error: error instanceof Error ? error.message : 'Unknown error',
      });
    }
  }
}

export const analysisService = new AnalysisService();

server/services/pdfExtractionService.ts.content:
import { readFile } from 'fs/promises';
import { join } from 'path';
import { createWorker } from 'tesseract.js';

export interface PageContent {
  pageNumber: number;
  text: string;
  method: 'digital' | 'ocr';
}

export interface ExtractionResult {
  pages: PageContent[];
  totalPages: number;
  extractionMethod: 'digital' | 'ocr' | 'hybrid';
  metadata?: {
    title?: string;
    author?: string;
    subject?: string;
    keywords?: string;
  };
}

class PDFExtractionService {
  private readonly MIN_TEXT_LENGTH = 50; // Threshold to determine if PDF has text

  async extractFromPath(filePath: string): Promise<ExtractionResult> {
    try {
      const absolutePath = filePath.startsWith('/') 
        ? filePath 
        : join(process.cwd(), filePath);

      const buffer = await readFile(absolutePath);
      return await this.extractFromBuffer(buffer);
    } catch (error) {
      console.error('PDF extraction error:', error);
      throw new Error(`Failed to extract PDF: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  async extractFromUrl(url: string): Promise<ExtractionResult> {
    try {
      // Validate URL format
      let validUrl: URL;
      try {
        validUrl = new URL(url);
      } catch {
        throw new Error(`Invalid URL format: ${url}`);
      }

      console.log(`Fetching PDF from URL: ${url}`);

      // Add browser-like headers to avoid 403 errors
      const response = await fetch(url, {
        headers: {
          'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/120.0.0.0 Safari/537.36',
          'Accept': 'application/pdf,application/octet-stream,*/*',
          'Accept-Language': 'en-US,en;q=0.9',
          'Referer': validUrl.origin,
        },
      });

      if (!response.ok) {
        throw new Error(`Failed to fetch PDF: ${response.status} ${response.statusText}`);
      }

      const arrayBuffer = await response.arrayBuffer();
      const buffer = Buffer.from(arrayBuffer);

      console.log(`PDF fetched successfully (${buffer.length} bytes), starting extraction...`);
      return await this.extractFromBuffer(buffer);
    } catch (error) {
      console.error('URL PDF extraction error:', error);
      throw new Error(`Failed to extract PDF from URL: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  async extractFromBuffer(buffer: Buffer): Promise<ExtractionResult> {
    // Try digital extraction first
    const digitalResult = await this.extractDigitalText(buffer);

    // Check if we got meaningful text
    const totalText = digitalResult.pages.reduce((sum, page) => sum + page.text.length, 0);

    if (totalText > this.MIN_TEXT_LENGTH * digitalResult.totalPages) {
      // Digital extraction successful
      return {
        ...digitalResult,
        extractionMethod: 'digital',
      };
    }

    // Fallback to OCR for scanned PDFs
    console.log('Minimal text found, using OCR...');
    const ocrResult = await this.extractWithOCR(buffer);

    return {
      ...ocrResult,
      extractionMethod: 'ocr',
      metadata: digitalResult.metadata,
    };
  }

  private async extractDigitalText(buffer: Buffer): Promise<Omit<ExtractionResult, 'extractionMethod'>> {
    const pdfParse = require('pdf-parse');
    const data = await pdfParse(buffer);

    // pdf-parse doesn't provide page-by-page text by default
    // We'll use the text and split it heuristically
    const pages: PageContent[] = [];
    const lines = data.text.split('\n');

    // Estimate page breaks (simple heuristic - can be improved)
    const linesPerPage = Math.ceil(lines.length / data.numpages) || 1;

    for (let i = 0; i < data.numpages; i++) {
      const start = i * linesPerPage;
      const end = Math.min((i + 1) * linesPerPage, lines.length);
      const pageText = lines.slice(start, end).join('\n');

      pages.push({
        pageNumber: i + 1,
        text: pageText,
        method: 'digital',
      });
    }

    return {
      pages,
      totalPages: data.numpages,
      metadata: {
        title: data.info?.Title,
        author: data.info?.Author,
        subject: data.info?.Subject,
        keywords: data.info?.Keywords,
      },
    };
  }

  private async extractWithOCR(buffer: Buffer): Promise<Omit<ExtractionResult, 'extractionMethod' | 'metadata'>> {
    const { fromBuffer } = await import('pdf2pic');
    const { writeFile, mkdir, rm } = await import('fs/promises');
    const { tmpdir } = await import('os');
    const { join } = await import('path');

    // Create temp directory for PDF conversion
    const tempDir = join(tmpdir(), `pdf-ocr-${Date.now()}`);
    await mkdir(tempDir, { recursive: true });

    try {
      // Get page count first
      const pdfParse = require('pdf-parse');
      const pdfData = await pdfParse(buffer);
      const totalPages = pdfData.numpages;

      // Configure pdf2pic
      const convert = fromBuffer(buffer, {
        density: 300, // High DPI for better OCR
        format: 'png',
        savePath: tempDir,
        saveFilename: 'page',
        width: 2000,
        height: 2000,
      });

      const pages: PageContent[] = [];
      const worker = await createWorker('eng');

      try {
        // Convert and OCR each page
        for (let pageNum = 1; pageNum <= totalPages; pageNum++) {
          console.log(`OCR processing page ${pageNum}/${totalPages}...`);

          // Convert page to image buffer
          const result = await convert(pageNum, { responseType: 'buffer' });

          if (!result.buffer) {
            throw new Error(`Failed to convert page ${pageNum} to image`);
          }

          // OCR the image
          const ocrResult = await worker.recognize(result.buffer);

          pages.push({
            pageNumber: pageNum,
            text: ocrResult.data.text,
            method: 'ocr',
          });
        }
      } finally {
        await worker.terminate();
      }

      return {
        pages,
        totalPages,
      };
    } finally {
      // Cleanup temp directory
      try {
        await rm(tempDir, { recursive: true, force: true });
      } catch (cleanupError) {
        console.warn('Failed to cleanup temp OCR directory:', cleanupError);
      }
    }
  }

  async extractMetadata(filePath: string): Promise<ExtractionResult['metadata']> {
    const absolutePath = filePath.startsWith('/') 
      ? filePath 
      : join(process.cwd(), filePath);

    const buffer = await readFile(absolutePath);
    const pdfParse = require('pdf-parse');
    const data = await pdfParse(buffer);

    return {
      title: data.info?.Title,
      author: data.info?.Author,
      subject: data.info?.Subject,
      keywords: data.info?.Keywords,
    };
  }
}

export const pdfExtractionService = new PDFExtractionService();

server/services/chunkingService.ts.content:
import type { PageContent } from './pdfExtractionService';

export interface ChunkAnchor {
  pageNumber: number;
  paragraphNumber: number;
  startChar: number;
  endChar: number;
}

export interface JudgmentChunk {
  chunkId: string;
  text: string;
  anchor: ChunkAnchor;
  metadata: {
    pageNumber: number;
    paragraphNumber: number;
    wordCount: number;
    charCount: number;
  };
}

export interface ChunkingResult {
  chunks: JudgmentChunk[];
  totalChunks: number;
  statistics: {
    totalPages: number;
    totalParagraphs: number;
    averageChunkSize: number;
  };
}

class ChunkingService {
  private readonly MAX_CHUNK_SIZE = 1000; // characters
  private readonly MIN_CHUNK_SIZE = 200; // characters
  private readonly OVERLAP_SIZE = 100; // character overlap between chunks

  async chunkPages(pages: PageContent[], judgmentId: string): Promise<ChunkingResult> {
    const chunks: JudgmentChunk[] = [];
    let globalParagraphCounter = 0;
    let chunkCounter = 0;

    for (const page of pages) {
      const paragraphs = this.extractParagraphs(page.text);

      for (let paraIndex = 0; paraIndex < paragraphs.length; paraIndex++) {
        const paragraph = paragraphs[paraIndex];

        // Skip empty paragraphs
        if (paragraph.trim().length === 0) continue;

        // Increment paragraph counter only for non-empty paragraphs
        globalParagraphCounter++;

        // If paragraph fits in one chunk, use it as is
        if (paragraph.length <= this.MAX_CHUNK_SIZE) {
          chunks.push({
            chunkId: `${judgmentId}-${page.pageNumber}-${globalParagraphCounter}-${chunkCounter++}`,
            text: paragraph,
            anchor: {
              pageNumber: page.pageNumber,
              paragraphNumber: globalParagraphCounter,
              startChar: 0,
              endChar: paragraph.length,
            },
            metadata: {
              pageNumber: page.pageNumber,
              paragraphNumber: globalParagraphCounter,
              wordCount: this.countWords(paragraph),
              charCount: paragraph.length,
            },
          });
        } else {
          // Split long paragraphs into overlapping chunks
          const paraChunks = this.splitLongParagraph(
            paragraph,
            page.pageNumber,
            globalParagraphCounter,
            judgmentId,
            chunkCounter
          );
          chunks.push(...paraChunks);
          chunkCounter += paraChunks.length;
        }
      }
    }

    // Calculate statistics
    const totalChunkSize = chunks.reduce((sum, chunk) => sum + chunk.text.length, 0);
    const averageChunkSize = chunks.length > 0 ? totalChunkSize / chunks.length : 0;

    return {
      chunks,
      totalChunks: chunks.length,
      statistics: {
        totalPages: pages.length,
        totalParagraphs: globalParagraphCounter,
        averageChunkSize: Math.round(averageChunkSize),
      },
    };
  }

  private extractParagraphs(text: string): string[] {
    // Split by double newlines (paragraph breaks) or single newlines followed by indentation
    // This preserves legal document structure
    const paragraphs: string[] = [];

    // First split by double newlines
    let parts = text.split(/\n\n+/);

    // Then check for numbered paragraphs or indented lines that might be separate paragraphs
    for (const part of parts) {
      // Check if this part contains numbered paragraphs (e.g., "1. ", "2. ", "(1)", "(a)")
      const numberedParts = part.split(/\n(?=\d+\.|\([0-9a-z]+\)|[A-Z]\.|[a-z]\))/);

      if (numberedParts.length > 1) {
        paragraphs.push(...numberedParts);
      } else {
        paragraphs.push(part);
      }
    }

    return paragraphs.filter(p => p.trim().length > 0);
  }

  private splitLongParagraph(
    paragraph: string,
    pageNumber: number,
    paragraphNumber: number,
    judgmentId: string,
    startChunkCounter: number
  ): JudgmentChunk[] {
    const chunks: JudgmentChunk[] = [];
    let position = 0;
    let localChunkIndex = 0;

    while (position < paragraph.length) {
      // Calculate chunk end position
      let endPosition = Math.min(position + this.MAX_CHUNK_SIZE, paragraph.length);

      // If not at the end, try to break at sentence boundary
      if (endPosition < paragraph.length) {
        // Look for sentence endings (. ! ?) within last 200 chars
        const searchStart = Math.max(position + this.MIN_CHUNK_SIZE, endPosition - 200);
        const searchText = paragraph.substring(searchStart, endPosition);
        const sentenceEnd = searchText.match(/[.!?]\s/);

        if (sentenceEnd && sentenceEnd.index !== undefined) {
          endPosition = searchStart + sentenceEnd.index + 2; // Include punctuation and space
        }
      }

      const chunkText = paragraph.substring(position, endPosition).trim();

      chunks.push({
        chunkId: `${judgmentId}-${pageNumber}-${paragraphNumber}-${startChunkCounter + localChunkIndex}`,
        text: chunkText,
        anchor: {
          pageNumber,
          paragraphNumber,
          startChar: position,
          endChar: endPosition,
        },
        metadata: {
          pageNumber,
          paragraphNumber,
          wordCount: this.countWords(chunkText),
          charCount: chunkText.length,
        },
      });

      localChunkIndex++;

      // If we've reached the end of the paragraph, exit loop
      if (endPosition >= paragraph.length) {
        break;
      }

      // Move position forward with overlap
      position = Math.max(endPosition - this.OVERLAP_SIZE, position + this.MIN_CHUNK_SIZE);
    }

    return chunks;
  }

  private countWords(text: string): number {
    return text.trim().split(/\s+/).length;
  }

  async chunkDocument(
    extractedText: string,
    judgmentId: string,
    pageNumber: number = 1
  ): Promise<ChunkingResult> {
    // For non-paginated text, treat as single page
    const pageContent: PageContent = {
      pageNumber,
      text: extractedText,
      method: 'digital',
    };

    return this.chunkPages([pageContent], judgmentId);
  }
}

